        githubAvatar: [],
        currentStatus: [] as string[],
        currentTrackUrl: [],
        currentTrackArtist: [] as string[]
      }
    },
    async fetch() {
      /* Defining usernames & API keys */
      const githubUserName:string = 'luisoos';

      const lastFmUserName:string = 'luisoos';
      const lastFmApiKey:string = 'a53048daf41428bd0b06800d1fbdb754';


      /* Fetching pinned repositories */
      this.repositories = await fetch(`https://gh-pinned-repos.egoist.sh/?username=${githubUserName}`).then(res => res.json())

      
      /* Fetching GitHub Avatar */
      const githubUser = await fetch(`https://api.github.com/users/${githubUserName}`).then(res => res.json())
      this.githubAvatar = githubUser.avatar_url


      /* Fetching currently playing track */
      const fetchUrl:string = `https://ws.audioscrobbler.com/2.0/?method=user.getrecenttracks&user=${lastFmUserName}&api_key=${lastFmApiKey}&format=json`;
    
      const response = await fetch(fetchUrl).then(res => res.json());

      const currentlyNotPlaying = response.recenttracks.track[0].date;

      const artistInformation = JSON.stringify(response.recenttracks.track[0].artist);
      const parsed = artistInformation.split(/[,]/);
      const artistText = parsed[parsed.length - 1];
      const splittedArtistText = artistText.split(/[:]/);
      const onlyArtist = splittedArtistText[splittedArtistText.length - 1];
      const splittedOnlyArtist = onlyArtist.split(/["]/);
      const artist:string = splittedOnlyArtist[1];

      if (currentlyNotPlaying == undefined) {
        this.currentStatus = response.recenttracks.track[0].name;
        this.currentTrackUrl = response.recenttracks.track[0].url;
        this.currentTrackArtist = [artist];
      } else {
        this.currentStatus = ['Not listening to anything!'];
      }
    }

  })
</script>
